UPDATE `locales_npc_text` SET `ID` = 14070,`Text0_0_loc3` = 'Wir können bald aufbrechen. Gebt uns nur noch ein bisschen Zeit, um unsere Vorbereitungen abzuschließen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14070;
