UPDATE `locales_npc_text` SET `ID` = 14131,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Einen weiteren Krieg mit der Horde müssen wir um jeden Preis vermeiden!' WHERE `locales_npc_text`.`ID` = 14131;
UPDATE `locales_npc_text` SET `ID` = 14134,`Text0_0_loc3` = 'Habt Ihr etwas, das festgeschraubt werden muss?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14134;
UPDATE `locales_npc_text` SET `ID` = 14137,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Wir haben bereits einige gute Leute hier oben verloren. Ich beginne daran zu zweifeln, ob dieser Ort unsere Aufmerksamkeit überhaupt wert ist.' WHERE `locales_npc_text`.`ID` = 14137;
UPDATE `locales_npc_text` SET `ID` = 14138,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Seid gegrüßt, $R. Eure Anwesenheit ehrt mich.$B$BWie kann ich Euch helfen?' WHERE `locales_npc_text`.`ID` = 14138;
UPDATE `locales_npc_text` SET `ID` = 14140,`Text0_0_loc3` = 'Bereitet Euch vor, $N. Wir greifen an, sobald all Eure Truppen eingetroffen sind!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14140;
UPDATE `locales_npc_text` SET `ID` = 14141,`Text0_0_loc3` = 'Das viele Kämpfen zeigt langsam Wirkung bei Euch, $N. Aber keine Angst, ich werde Euer kleines Geheimnis für mich behalten, damit die anderen Euch nicht für verrückt halten.$B$BRuht Euch etwas aus, das wird Euch gut tun.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14141;
UPDATE `locales_npc_text` SET `ID` = 14142,`Text0_0_loc3` = 'Die letzte Schlacht wird bald beginnen, $N. Still gestanden!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14142;
UPDATE `locales_npc_text` SET `ID` = 14149,`Text0_0_loc3` = 'Wir heißen Euch hier herzlich willkommen, $G Freund und Verbündeter:Freundin und Verbündete; der Drachenschwärme. Ruht Euch in unseren Hallen aus.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14149;
UPDATE `locales_npc_text` SET `ID` = 14150,`Text0_0_loc3` = 'Ah, Ihr seid''s, $N! Willkommen in unserem Heiligtum. Erholt und erfrischt Euch; Ihr habt es Euch mehr als verdient.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14150;
UPDATE `locales_npc_text` SET `ID` = 14151,`Text0_0_loc3` = '<Die Leibwache der Königin nickt in Anerkennung Eurer Anwesenheit.>',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14151;
UPDATE `locales_npc_text` SET `ID` = 14155,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'War ein geschäftiger Tag in der Luft, was, $N? Vielleicht solltet Ihr einen Moment Luft holen, bevor Ihr wieder startet.' WHERE `locales_npc_text`.`ID` = 14155;
UPDATE `locales_npc_text` SET `ID` = 14158,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Ich kann Euch sagen, es macht wirklich Spaß, Euch an der Arbeit zu sehen! Und Ihr habt wirklich einen hohen Verbrauch an Bombern!' WHERE `locales_npc_text`.`ID` = 14158;
UPDATE `locales_npc_text` SET `ID` = 14159,`Text0_0_loc3` = 'Vorsicht, $N. Wo ist Eure Kultistenkapuze?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14159;
UPDATE `locales_npc_text` SET `ID` = 14160,`Text0_0_loc3` = 'Da seid Ihr ja. Seid Ihr bereit? Wenn wir einmal angefangen haben, wird es kein Zurück mehr geben.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14160;
UPDATE `locales_npc_text` SET `ID` = 14161,`Text0_0_loc3` = 'Willkommen in der Heimat der Kirin Tor.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14161;
UPDATE `locales_npc_text` SET `ID` = 14162,`Text0_0_loc3` = 'Willkommen im Zauberkasten, $C. Ich hoffe, Ihr werdet Euren Aufenthalt hier genießen.',`Text0_1_loc3` = 'Willkommen im Zauberkasten, $C. Ich hoffe, Ihr werdet Euren Aufenthalt hier genießen.' WHERE `locales_npc_text`.`ID` = 14162;
UPDATE `locales_npc_text` SET `ID` = 14163,`Text0_0_loc3` = 'Willkommen in der Taverne ''Zur Zauberkrähe'', $R.$B$BEin Wort der Warnung: Seht Euch hier besser vor. Narisa mag es nicht besonders, Blut vom Boden wischen zu müssen.',`Text0_1_loc3` = 'Willkommen in der Taverne ''Zur Zauberkrähe'', $R.$B$BEin Wort der Warnung: Seht Euch hier besser vor. Narisa mag es nicht besonders, Blut vom Boden wischen zu müssen.' WHERE `locales_npc_text`.`ID` = 14163;
UPDATE `locales_npc_text` SET `ID` = 14165,`Text0_0_loc3` = 'Solltet Ihr abgeschossen und gefangen werden, dann nennt nicht Euren richtigen Namen. Benutzt einen Codenamen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14165;
UPDATE `locales_npc_text` SET `ID` = 14168,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Die meisten Drachen hätten Angst davor, sich hier zu bewegen, $R.$B$BWas kann einen Sterblichen nur dazu bewegt haben, an einen solch tückischen Ort zu kommen?' WHERE `locales_npc_text`.`ID` = 14168;
UPDATE `locales_npc_text` SET `ID` = 14169,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Habt Ihr den Mut, Euch den Elitedrachen von Malygos in ihrem eigenen Reich zu stellen, $R?' WHERE `locales_npc_text`.`ID` = 14169;
UPDATE `locales_npc_text` SET `ID` = 14170,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Meine Drachen haben Eure Rückkehr begierig erwartet, $C.$B$BSeid Ihr bereit, loszufliegen?' WHERE `locales_npc_text`.`ID` = 14170;
UPDATE `locales_npc_text` SET `ID` = 14172,`Text0_0_loc3` = 'Entschuldigt, $GFreund:Freundin;. Nur ausgewiesene Offiziere der Allianz können den Kauf eines Fahrzeugs autorisieren.',`Text0_1_loc3` = 'Entschuldigt, $GFreund:Freundin;. Nur ausgewiesene Offiziere der Allianz können den Kauf eines Fahrzeugs autorisieren.' WHERE `locales_npc_text`.`ID` = 14172;
UPDATE `locales_npc_text` SET `ID` = 14174,`Text0_0_loc3` = 'Diese uralten Folianten können immer in der Nähe von Inschriftenkundelehrern gefunden werden.',`Text0_1_loc3` = 'Diese uralten Folianten können immer in der Nähe von Inschriftenkundelehrern gefunden werden.' WHERE `locales_npc_text`.`ID` = 14174;
UPDATE `locales_npc_text` SET `ID` = 14176,`Text0_0_loc3` = 'Wir haben es geschafft! Ist es zu glauben, was wir hier hören? Es ist kaum zu fassen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14176;
UPDATE `locales_npc_text` SET `ID` = 14177,`Text0_0_loc3` = 'Ist es nicht phantastisch? Könnt Ihr den möglichen Profit sehen?',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14177;
UPDATE `locales_npc_text` SET `ID` = 14178,`Text0_0_loc3` = 'Ein wirklich schöner Tag, findet Ihr nicht? Ich hoffe, wir sehen noch ein wenig Feuerwerk, bevor der Tag sich dem Ende neigt.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14178;
