UPDATE `locales_npc_text` SET `ID` = 14182,`Text0_0_loc3` = 'Die I.D.S.V.-Ausgabe poltert erwartungsvoll.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14182;
UPDATE `locales_npc_text` SET `ID` = 14192,`Text0_0_loc3` = 'Willkommen in meinem bescheidenen Heim, $C. Ich empfinde diesen Ort als sehr entspannend und fand es immer großartig, in Dalaran zu Hause zu sein. Manchmal ist es allerdings schon ein wenig einsam hier... ziemlich einsam...',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14192;
UPDATE `locales_npc_text` SET `ID` = 14194,`Text0_0_loc3` = 'Verblüffend! Hier sind Passagen, die ich zuvor noch nie gesehen habe. Das Ausfindigmachen muss Euch einiges an Zeit gekostet haben. Solltet Ihr noch mehr solcher Bücher finden, so wäre ich glücklich, wenn Ihr mir sofort Bescheid geben könntet.$B$BBitte nehmt einen meiner Familiare der Kirin Tor mit Euch, für den Fall, dass Ihr über etwas stolpert.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14194;
UPDATE `locales_npc_text` SET `ID` = 14197,`Text0_0_loc3` = 'Eine zünftige Schatzjagd und ein wenig herzhaftes Bier - besser geht''s nicht, $G Kumpel:meine Liebe;.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14197;
UPDATE `locales_npc_text` SET `ID` = 14198,`Text0_0_loc3` = 'Der Smaragdgrüne Traum ist zu einem gefährlichen Ort geworden. Ich mache mir mehr und mehr Sorgen um diejenigen, die ihr Leben im Wachzustand in Azeroth opfern, in der Hoffnung, es zu retten.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14198;
UPDATE `locales_npc_text` SET `ID` = 14203,`Text0_0_loc3` = 'Die Hämmer der Irdenen stehen geschlossen hinter Bergfels. Wir werden uns Lokens Günstlingen bis zum Ende entgegenwerfen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14203;
UPDATE `locales_npc_text` SET `ID` = 14204,`Text0_0_loc3` = 'Kommt auf den Punkt, $C.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14204;
UPDATE `locales_npc_text` SET `ID` = 14205,`Text0_0_loc3` = '<Der Motor rumpelt laut.>',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14205;
