UPDATE `locales_npc_text` SET `ID` = 14208,`Text0_0_loc3` = 'Endlich!$B$B$GDer:Die:c; $C aus den Prophezeiungen ist angekommen!',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14208;
