UPDATE `locales_npc_text` SET `ID` = 14236,`Text0_0_loc3` = 'Ja, Mann, das is'' die Zeit, in der man froh sein soll wegen dem Leben und dem Frühling. Haste schon ordentlich Eier gesammelt?',`Text0_1_loc3` = 'Ja, Mann, das is'' die Zeit, in der man froh sein soll wegen dem Leben und dem Frühling. Haste schon ordentlich Eier gesammelt?' WHERE `locales_npc_text`.`ID` = 14236;
