UPDATE `locales_npc_text` SET `ID` = 14240,`Text0_0_loc3` = 'Seid gegrüßt, $C.$B$BWenn die Allianz die Tausendwinterfestung kontrolliert, werde ich ein Portal öffnen für diejenigen, die dorthin reisen möchten. $B$BDie Schlacht läuft gerade. Macht Euch auf nach Tausendwinter und unterstützt unsere Truppen!',`Text0_1_loc3` = 'Seid gegrüßt, $C.$B$BWenn die Allianz die Tausendwinterfestung kontrolliert, werde ich ein Portal öffnen für diejenigen, die dorthin reisen möchten. $B$BDie Schlacht läuft gerade. Macht Euch auf nach Tausendwinter und unterstützt unsere Truppen!' WHERE `locales_npc_text`.`ID` = 14240;
UPDATE `locales_npc_text` SET `ID` = 14241,`Text0_0_loc3` = 'Seid gegrüßt, $C.$B$BWenn die Horde die Tausendwinterfestung kontrolliert, werde ich ein Portal öffnen für diejenigen, die dorthin reisen möchten. $B$BDie Schlacht läuft gerade. Macht Euch auf nach Tausendwinter und unterstützt unsere Truppen!',`Text0_1_loc3` = 'Seid gegrüßt, $C.$B$BWenn die Horde die Tausendwinterfestung kontrolliert, werde ich ein Portal öffnen für diejenigen, die dorthin reisen möchten. $B$BDie Schlacht läuft gerade. Macht Euch auf nach Tausendwinter und unterstützt unsere Truppen!' WHERE `locales_npc_text`.`ID` = 14241;
