UPDATE `locales_npc_text` SET `ID` = 14534,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Unter der grausamen Herrschaft des Lichkönigs haben alle Einwohner Nordends schwer leiden müssen, insbesondere die Kinder. Wenn Ihr mit den Orakeln oder den Wildherzen aus dem Sholazarbecken befreundet seid, überlegt Euch doch, Euch vielleicht eine Woche lang um ein Waisenkind zu kümmern.' WHERE `locales_npc_text`.`ID` = 14534;
