UPDATE `locales_npc_text` SET `ID` = 14621,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Hey, Herzchen. Der Zeppelin, der hier hält, bringt Euch direkt nach Donnerfels. Kostenlos. Wer braucht schon Windreiter, wenn wir Goblins hier sind, hab ich nicht Recht?' WHERE `locales_npc_text`.`ID` = 14621;
