UPDATE `locales_npc_text` SET `ID` = 14713,`Text0_0_loc3` = NULL,`Text0_1_loc3` = 'Nein, ich bin hier die Intellektuelle und man sollte mich nicht auf die leichte Schulter nehmen. Ich könnte einen Heiltrank aus nichts anderem als Abwaschwasser und Pusteblumen herstellen, allerdings werde ich Euch das ganz bestimmt nicht zeigen!$B$BWie dem auch sei, solltet Ihr einen Einkauf tätigen wollen, dann wendet Euch bitte an Patricia.' WHERE `locales_npc_text`.`ID` = 14713;
UPDATE `locales_npc_text` SET `ID` = 14723,`Text0_0_loc3` = 'Die Geister der Elemente sind weise. Sie waren lange vor uns hier und sie werden fortbestehen, lange nachdem wir verschwunden sind. Wenn sie zu uns sprechen, müssen wir zuhören.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14723;
