UPDATE `locales_npc_text` SET `ID` = 14777,`Text0_0_loc3` = 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Horde die Tausendwinterfestung, doch die Schlacht hat begonnen! Macht Euch auf nach Tausendwinter und unterstützt unsere Truppen!',`Text0_1_loc3` = 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Horde die Tausendwinterfestung, doch die Schlacht hat begonnen! Macht Euch auf nach Tausendwinter und unterstützt unsere Truppen!' WHERE `locales_npc_text`.`ID` = 14777;
UPDATE `locales_npc_text` SET `ID` = 14779,`Text0_0_loc3` = 'Wir haben uns schon seit einiger Zeit um diese Würmer gekümmert. Ich hoffe, dass sie rechtzeitig fit sein werden, um an der Ringveranstaltung dieser Organisation teilzunehmen. Ihr müsst Euch immerhin nicht einem, nein, zwei Jormungars stellen.',`Text0_1_loc3` = NULL WHERE `locales_npc_text`.`ID` = 14779;
